// src/components/auth/LoginForm.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../../services/api";
import { useAuth } from "../../context/AuthContext";
import Input from "../ui/Input";
import Button from "../ui/Button";

function LoginForm() {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [userid, setUserid] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isLoading) return;
    setError("");

    if (!userid || !password) {
      setError("Please enter both User ID and Password.");
      return;
    }

    setIsLoading(true);

    try {
      const res = await api.post("/auth/login", { userid, password });
      const userData = res.data;

      const me = await login(userData);

      if ((me.role || userData.role || "").toUpperCase() === "ADMIN") {
        navigate("/admin");
      } else {
        const allocs = me.allocations || [];
        const hasMarks = allocs.some((a) => (a.subject_eval_type || "MARKS") === "MARKS");
        const hasPE = allocs.some((a) => a.subject_eval_type === "GRADE" && a.subject_code === "PE");
        const hasEVS = allocs.some((a) => a.subject_eval_type === "GRADE" && a.subject_code === "EVS");

        if (!hasMarks) {
          if (hasPE && !hasEVS) {
            navigate("/teacher/pe-grade");
            return;
          }
          if (hasEVS && !hasPE) {
            navigate("/teacher/evs-grade");
            return;
          }
        }

        navigate("/teacher");
      }
    } catch (err) {
      let msg = "Login failed. Please try again.";

      if (err?.response?.data?.error) {
        msg = err.response.data.error;
      } else if (err?.response?.data?.message) {
        msg = err.response.data.message;
      } else if (err?.response?.status === 401) {
        msg = "Invalid credentials. Please check your inputs.";
      } else if (err?.response?.status === 500) {
        msg = "Server unavailable. Please try again later.";
      }

      setError(msg);
      console.error("Login error:", err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ width: '100%', maxWidth: '400px', margin: '0 auto' }}>
      <Input
        label="User ID"
        value={userid}
        onChange={(e) => setUserid(e.target.value)}
        placeholder="Enter your ID"
        required
      />

      <div style={{ position: "relative" }}>
        <Input
          label="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Enter your password"
          type={showPassword ? "text" : "password"}
          required
        />
        <button
          type="button"
          onClick={() => setShowPassword(!showPassword)}
          style={{
            position: "absolute",
            right: "15px",
            top: "38px", // Adjusted for label height + padding
            background: "none",
            border: "none",
            cursor: "pointer",
            fontSize: "1.2rem",
            color: "var(--text-muted)",
            opacity: 0.7,
            transition: "opacity 0.2s"
          }}
          onMouseEnter={(e) => e.target.style.opacity = 1}
          onMouseLeave={(e) => e.target.style.opacity = 0.7}
        >
          {showPassword ? "üëÅÔ∏è" : "üëÅÔ∏è‚Äçüó®Ô∏è"}
        </button>
      </div>

      <div style={{ marginTop: '1.5rem' }}>
        <Button 
          type="submit" 
          fullWidth 
          disabled={isLoading}
        >
          {isLoading ? 'Logging In...' : 'Login'}
        </Button>
      </div>
      
      {error && (
        <div 
          style={{ 
            marginTop: '1.5rem', 
            color: '#ef4444', 
            background: 'rgba(239, 68, 68, 0.1)', 
            padding: '1rem', 
            borderRadius: 'var(--radius-sm)',
            fontSize: '0.9rem' 
          }}
          className="animate-fade-in"
        >
          {error}
        </div>
      )}
    </form>
  );
}

export default LoginForm;
