import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../../services/api';
import { REPORT_CONFIG } from '../../reportConfig';
import { useAuth } from '../../context/AuthContext';

export default function StatementOfMarks() {
  useAuth();
  const { division, rollNo } = useParams();
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchData() {
      try {
        const res = await api.get('/admin/results', {
          params: { division, roll_no: rollNo }
        });
        // result could be an array or object depending on backend response for single item
        // Based on analysis, single object is expected if array length is 1, but let's be safe
        let resultData = res.data;
        if (Array.isArray(resultData) && resultData.length > 0) {
          resultData = resultData[0];
        } else if (Array.isArray(resultData) && resultData.length === 0) {
            resultData = null;
        }

        setData(resultData);
      } catch (err) {
        console.error("Failed to fetch result", err);
      } finally {
        setLoading(false);
      }
    }
    fetchData();
  }, [division, rollNo]);

  if (loading) return <div>Loading report...</div>;
  if (!data) return <div>Student not found.</div>;

  // -- Helper to safely get nested mark per subject --
  // The backend returns subject.mark = { unit1, unit2, term, annual, ... }
  const getMark = (subjCode, field) => {
    const subj = data.subjects.find(s => s.code === subjCode);
    if (!subj || !subj.mark) return "";
    return subj.mark[field] ?? "";
  };

  const getSubjParams = (subjCode) => {
      const subj = data.subjects.find(s => s.code === subjCode);
      return subj;
  }

  // Identify core subjects vs optionals
  // We want to force a specific order: English, Hindi/IT, Economics, BK, OC, SP/Maths
  // Then Grade subjects: EVS, PE
  
  // NOTE: This mapping logic is somewhat specific to the report structure.
  // To keep it flexible, we iterate the data.subjects but sort them by a known config if possible.
  
  // For now, let's map the `data.subjects` to the rows.
  // We need to know which subject is in which slot to handle the "OR" logic (like Hindi vs IT) 
  // but the backend `data.subjects` already contains ONLY the subjects the student has.
  
  // Custom Sorter based on standard report order
  const ORDER = ["ENG", "HINDI", "IT", "MARATHI", "FRENCH", "GERMAN", "ECO", "BK", "OC", "SP", "MATHS"];
  const GRADE_SUBJECTS = ["EVS", "PE"];

  const numericSubjects = data.subjects.filter(s => !GRADE_SUBJECTS.includes(s.code));
  const gradeSubjects = data.subjects.filter(s => GRADE_SUBJECTS.includes(s.code));

  // Sort numeric subjects
  numericSubjects.sort((a, b) => {
    const ia = ORDER.indexOf(a.code);
    const ib = ORDER.indexOf(b.code);
    return (ia === -1 ? 99 : ia) - (ib === -1 ? 99 : ib);
  });
  
  // Sort grade subjects (EVS first, then PE)
  gradeSubjects.sort((a, b) => {
      if (a.code === 'EVS') return -1;
      if (b.code === 'EVS') return 1;
      return 0;
  });

  // Calculate totals
  // Max marks logic: 
  // UT1: 25, Terminal: 50, UT2: 25, Annual: 100, Total: 100? No, Average is scaled to 100.
  // Wait, the header says: UT-I (25), Terminal (50), UT-II (25), Annual (100).
  // Then "Average" (Max 100).
  // The 'Average' column seems to be the final subject average (which is usually (U1+T+U2+A) / ... or just the `avg` field from backend)
  // Backend `avg` is the computed average scaled to 100.

  return (
    <div className="print-container" style={{ 
      fontFamily: '"Times New Roman", Times, serif', 
      padding: '20px', 
      maxWidth: '1000px', 
      margin: '0 auto',
      color: '#000'
    }}>
      <style>{`
        @media print {
          @page { size: landscape; margin: 10mm; }
          body { -webkit-print-color-adjust: exact; }
          .no-print { display: none !important; }
        }
        .report-table {
          width: 100%;
          border-collapse: collapse;
          border: 1px solid #000;
        }
        .report-table th, .report-table td {
          border: 1px solid #000;
          padding: 4px 8px;
          text-align: center;
          font-size: 14px;
        }
        .report-table th {
            font-weight: bold;
        }
        .left-align { text-align: left !important; }
        .bold { font-weight: bold; }
      `}</style>

      {/* Button to Print */}
      <div className="no-print" style={{ marginBottom: 20 }}>
        <button onClick={() => window.print()} style={{ padding: '8px 16px', fontSize: 16, cursor: 'pointer' }}>
          Print Report
        </button>
      </div>

      {/* Header */}
      <div style={{ textAlign: 'center', marginBottom: '10px' }}>
        <h2 style={{ margin: '0 0 5px 0', textTransform: 'uppercase' }}>{REPORT_CONFIG.schoolName}</h2>
        <div style={{ fontSize: '12px', marginBottom: '10px', textTransform: 'uppercase' }}>{REPORT_CONFIG.schoolAddress}</div>
        <h3 style={{ margin: '5px 0', textDecoration: 'underline' }}>{REPORT_CONFIG.reportTitle}</h3>
        <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '14px', marginTop: '15px', fontWeight: 'bold' }}>
             <div>INDEX NO: J16.15.017 &nbsp;&nbsp;&nbsp; UDISE NO: 27211007406</div> 
             {/* Note: Index/UDISE seem static in the image, keeping hardcoded or moved to config if needed. 
                 If user didn't ask for them to be dynamic, I'll keep them static or generic. 
                 Ideally these should be in config too. 
             */}
        </div>
        <div style={{ fontWeight: 'bold', fontSize: '14px', marginTop: '5px' }}>
             EXAMINATION HELD IN {REPORT_CONFIG.examheldIn} 
             <span style={{ float: 'right' }}>Date: &nbsp;&nbsp;/&nbsp;&nbsp;&nbsp;/20&nbsp;&nbsp;</span>
        </div>
      </div>

      {/* Student Info Table */}
      <table className="report-table" style={{ marginBottom: '5px' }}>
        <tbody>
          <tr>
            <td className="left-align bold" style={{ width: '20%' }}>Examination Seat No</td>
            <td className="left-align bold" style={{ width: '30%' }}>{data.roll_no}</td>
            <td className="left-align bold" style={{ width: '50%' }}>Result/ Grade</td>
          </tr>
          <tr>
            <td className="left-align bold">{/* Placeholder for seat no text if needed, otherwise empty row as per image */} &nbsp; </td>
            <td className="left-align"></td>
            <td className="left-align bold">
                {/* Overall Result */}
                {data.overall_grade ? data.overall_grade : 
                 (data.percentage >= 35 ? "PASS" : "FAIL")}
            </td>
          </tr>
        </tbody>
      </table>

      {/* Marks Table */}
      <table className="report-table">
        <thead>
          <tr>
            <th rowSpan={2} style={{ width: '25%' }}>SUBJECTS</th>
            <th colSpan={2}>U.T - I</th>
            <th colSpan={2}>TERMINAL</th>
            <th colSpan={2}>U.T. - II</th>
            <th colSpan={2}>ANNUAL</th>
            <th colSpan={4}>AVERAGE</th>
          </tr>
          <tr>
            {/* Sub headers */}
            <th style={{ width: '5%' }}>MAX</th><th style={{ width: '5%' }}>OBTN</th>
            <th style={{ width: '5%' }}>MAX</th><th style={{ width: '5%' }}>OBTN</th>
            <th style={{ width: '5%' }}>MAX</th><th style={{ width: '5%' }}>OBTN</th>
            <th style={{ width: '5%' }}>MAX</th><th style={{ width: '5%' }}>OBTN</th>
            
            <th style={{ width: '5%' }}>MAX</th>
            <th style={{ width: '5%' }}>MIN</th>
            <th style={{ width: '5%' }}>AVG</th>
            <th style={{ width: '5%' }}>Grace</th>
          </tr>
        </thead>
        <tbody>
          {/* Numeric Subjects Rows */}
          {numericSubjects.map(subj => (
            <tr key={subj.code}>
              <td className="left-align bold">
                  {/* Map Codes to Full Names if possible, else use Code */}
                  {/* A simple map for standard codes, or just use what backend sends if it has names. 
                      Backend sends 'code'. We might need a name mapping in config or utilize backend subject_name if available.
                      Backend 'Result' object doesn't have subject names directly in 'subjects' array, only codes.
                      For now, use Code or a simple mapping.
                  */}
                  {getSubjName(subj.code)}
              </td>
              
              {/* UT1 */}
              <td>25</td><td>{subj.mark?.unit1 ?? "-"}</td>
              {/* Terminal */}
              <td>50</td><td>{subj.mark?.term ?? "-"}</td>
              {/* UT2 */}
              <td>25</td><td>{subj.mark?.unit2 ?? "-"}</td>
              {/* Annual */}
              <td>100</td><td>{subj.mark?.annual ?? "-"}</td>
              
              {/* Average */}
              <td>100</td>
              <td>35</td>
              <td className="bold">{Math.round(subj.avg)}</td> {/* Rounding as per requirement */}
              <td>{subj.grace > 0 ? subj.grace : ""}</td>
            </tr>
          ))}

          {/* Total Row */}
          <tr style={{ height: '30px' }}>
            <td className="left-align bold">Total Marks (out of {numericSubjects.length * 100})</td>
            <td colSpan={8}></td> {/* Empty cells under unit/term/annual */}
            
            <td className="bold">{numericSubjects.reduce((acc, s) => acc + (s.avg || 0), 0) + (data.total_grace || 0) }</td> {/* Total Avg + Grace? Or just Final Total? */}
            {/* The image shows Total Marks row spanning the columns? 
                Actually image shows "Total Marks (out of 600)" in Subjects col, 
                and then empty cells until checking the Average column? 
                Actually the 'Average' - 'OBTN' column is not there. It is MAX | MIN | AVG | Grace.
                So the sum should be under AVG.
            */}
            <td></td> {/* Grace col in total row? usually empty or total grace */}
          </tr>
          
          {/* Grade Subjects (EVS, PE) */}
          {/* They appear AFTER Valid Total row in the image */}
          {gradeSubjects.map(subj => (
             <tr key={subj.code}>
                 <td className="left-align bold">{getSubjName(subj.code)}</td>
                 <td colSpan={8}></td>
                 <td className="bold">{subj.grade}</td>
                 <td></td>
             </tr>
          ))}

        </tbody>
      </table>

      {/* Footer */}
      <div style={{ marginTop: '50px', display: 'flex', justifyContent: 'space-between', padding: '0 20px' }}>
          {REPORT_CONFIG.signatories.map((sig, idx) => (
              <div key={idx} style={{ textAlign: 'center' }}>
                  <div style={{ height: '40px' }}></div> {/* Space for signature */}
                  <div style={{ fontWeight: 'bold', fontSize: '12px' }}>{sig.label}</div>
              </div>
          ))}
      </div>
      
      {/* Grading Key */}
      <div style={{ marginTop: '30px', fontSize: '10px', lineHeight: '1.4' }}>
          {REPORT_CONFIG.gradingInfo.map((line, idx) => (
              <div key={idx}>{line}</div>
          ))}
      </div>
    </div>
  );
}

// Helper to map codes to nice names
// Use a robust list or if possible fetch from backend 'Subject' list, 
// but for this component we might just hardcode common ones or use the code.
function getSubjName(code) {
    const MAP = {
        "ENG": "English",
        "HINDI": "Hindi",
        "IT": "Information Technology",
        "ECO": "Economics",
        "BK": "Book Keeping and Accountancy",
        "OC": "Organisation of Commerce and Management",
        "SP": "Secretarial Practice",
        "MATHS": "Mathematics",
        "EVS": "Environment Education & Water Security",
        "PE": "Physical Education"
    };
    return MAP[code] || code;
}
