import React from 'react';
import styled from 'styled-components'; // Wait, I didn't verify if styled-components is installed. 
// The plan said "Vanilla CSS" mostly or "Glassmorphism utility classes". 
// I should stick to CSS Modules or just standard CSS/classes to avoid adding dependency if not needed.
// Package.json didn't show styled-components.
// I will use the global CSS classes I just defined + inline styles or a new CSS file if needed.
// Actually, simple BEM or utility classes from index.css is better.

const Input = ({ label, type = "text", value, onChange, placeholder, required = false, ...props }) => {
  return (
    <div style={{ marginBottom: '1.5rem', width: '100%' }}>
      {label && (
        <label 
          style={{ 
            display: 'block', 
            marginBottom: '0.5rem', 
            color: 'var(--text-secondary)',
            fontSize: '1rem',
            fontWeight: '500'
          }}
        >
          {label}
        </label>
      )}
      <input
        type={type}
        className="glass-input"
        value={value}
        onChange={onChange}
        placeholder={placeholder}
        required={required}
        style={{
          width: '100%',
          padding: '1.25rem',
          fontSize: '1.1rem',
          borderRadius: 'var(--radius-md)',
          // "Big simple box" feel
        }}
        {...props}
      />
    </div>
  );
};

export default Input;
