// src/pages/Admin/AdminDashboard.jsx
import React from "react";
import { useNavigate } from "react-router-dom";
import Sidebar from "../../components/Layout/Sidebar";
import Card from "../../components/UI/Card";

export default function AdminDashboard() {
  const navigate = useNavigate();

  const menu = [
    { key: 'teachers', label: 'Manage Teachers', to: '/admin/teachers' },
    { key: 'view-results', label: 'View Complete Result', to: '/admin/results' },
    { key: 'alloc', label: 'Allocate Subjects', to: '/admin/allocations' },
    { key: 'add-student', label: 'Add Student', to: '/admin/students/add' },
    { key: 'view-students', label: 'View Students', to: '/admin/students' },
    { key: 'import-students', label: 'Import Students', to: '/admin/students/import' },
  ];

  const quickActions = [
    { title: 'Manage Teachers', desc: 'Add, edit or remove teacher accounts', path: '/admin/teachers' },
    { title: 'Allocate Subjects', desc: 'Assign teachers to subjects and divisions', path: '/admin/allocations' },
    { title: 'Add Student', desc: 'Create student records quickly', path: '/admin/students/add' },
    { title: 'View Students', desc: 'Browse and export student lists', path: '/admin/students' },
  ];

  return (
    <div className="page-container" style={{ paddingLeft: '340px' }}>
      <Sidebar links={menu} />
      
      <main className="animate-fade-in">
        <h1 style={{ marginBottom: '2rem' }}>Dashboard Overview</h1>

        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', 
          gap: '2rem' 
        }}>
          {quickActions.map((action) => (
            <Card 
              key={action.path} 
              style={{ cursor: 'pointer', transition: 'transform 0.2s' }}
              onClick={() => navigate(action.path)}
              onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-5px)'}
              onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}
            >
              <h3 style={{ marginBottom: '0.5rem', color: 'var(--accent-primary)' }}>{action.title}</h3>
              <p style={{ color: 'var(--text-secondary)', margin: 0 }}>{action.desc}</p>
            </Card>
          ))}
        </div>
      </main>
    </div>
  );
}
